osx.snippet:
 (-moz-bool-pref: browser.theme.native-theme) {
  media not (prefers-contrast) {
    :root:not([lwtheme]) {
      media (prefers-color-scheme: light) {
        --urlbar-box-bgcolor: fafafa;
      }
      media (prefers-color-scheme: dark) {
        --tab-selected-bgcolor: color-mix(in srgb, -moz-dialog 75.0, white);
      }
    }
  }
}

.browser-titlebar {
  /* Centrally align content items vertically */
  justify-content: center;
}

.titlebar-buttonbox-container {
  align-items: center;
  /* Hide window controls in fullscreen */
  :root[inFullscreen] & {
    display: none;
  }
}

.titlebar-buttonbox {
  &:-moz-locale-dir(ltr) {
    margin-inline: 12.0 0.0;

    media (-moz-mac-rtl) {
      margin-inline: 12.0;
    }
  }

  &:-moz-locale-dir(rtl) {
    margin-inline: 12.0;

    media (-moz-mac-rtl) {
      margin-inline: 12.0 0.0;
    }
  }
}

nav-bar-customization-target > wrapper-personal-bookmarks > personal-bookmarks {
  min-height: 32.0;
  align-items: center;
}

 (prefers-contrast) {
  /* The "increase contrast" preference on macOS draws a 1px black line around
     panels, including the separate search bar. Since the Urlbar is not
     implemented as a panel, it does not get this outline. This outline rule
     resembles the system outline, bringing the Urlbar in line with panels and
     other Mac apps. */
  urlbar[open] > urlbar-background {
    outline: 1.0 solid var(--focus-outline-color)
  }
}

editBMPanel_folderTree,
editBMPanel_tagsSelector {
  appearance: none;
  background-color: var(--arrowpanel-field-background);
  color: inherit;
  border-radius: 2.0;
  border: 1.0 solid var(--panel-separator-color);
  margin: 0.0;
}

editBMPanel_folderTree > treechildren::-moz-tree-twisty(blur,selected),
editBMPanel_folderTree > treechildren::-moz-tree-image(blur,selected),
editBMPanel_folderTree > treechildren::-moz-tree-cell-text(blur,selected),
editBMPanel_tagsSelector:not(:focus) > richlistitem[selected] {
  color: inherit;
}

editBMPanel_folderTree {
  border-bottom: none;
  border-end-start-radius: 0.0;
  border-end-end-radius: 0.0;
  /* Implements editBookmarkPanel resizing on folderTree un-collapse. */
  margin: 0.0 !important;
  min-width: 27.0;
  position: relative;
}

sidebar-box {
  /* Default font size is 11px on mac, so this is 12px */
  font-size: 1.0909;
}

.tabbrowser-tab {
  border: none;

  /* FIXME: Are these styles really needed nowadays? */
  &:is([visuallyselected], [multiselected]) {
    /* overriding tabbox.css */
    text-shadow: inherit;

    media (prefers-color-scheme: light) {
      :root:not([lwtheme]) & {
        /* overriding tabbox.css */
        color: hsl(240.0, 5.0, 5.0);
      }
    }
  }
}

tabbrowser-tabs {
  /* overriding tabbox.css */
  font: message-box;
  align-items: stretch;
  margin-bottom: 0.0;
  position: static;
  z-index: auto;
}

shared.snippet:
.actionsMenu > menupopup > menuitem > .menu-iconic-left {
  /* Undo content/browser/preferences/handlers.css - we don't
   * want icon-less labels to line up with the other labels.
   */
  min-width: 0.0;
}

appList > richlistitem > image {
  margin: 5.0;
  width: 32.0;
  height: 32.0;
}

appList > richlistitem > label {
  margin: 0.0;
  padding: 5.0;
  white-space: nowrap;
}

.panel-subview-body {
  scrollbar-color: color-mix(in srgb, currentColor 26.0, transparent) transparent;
}

.panel-button {
  appearance: none;
  border-radius: 2.0;
  background-color: var(--arrowpanel-dimmed);
  color: inherit;
  padding: 2.0 6.0;
}

placesToolbar {
  position: relative;
  -moz-window-dragging: drag;
  padding: env(-moz-mac-titlebar-height) 4.0 3.0;
  border-bottom: 1.0 solid ThreeDShadow;

  &::after {
    content: ;
    position: absolute;
    inset: 0.0;
    appearance: auto;
    -moz-default-appearance: -moz-window-titlebar;
    z-index: -1.0;
  }

  > toolbarbutton {
    margin: 4.0 4.0 5.0;
    padding: 0.0;
    height: 22.0;
    appearance: auto;
    -moz-default-appearance: toolbarbutton;

    > .toolbarbutton-icon {
      -moz-context-properties: fill, fill-opacity;
      fill: currentColor;
      fill-opacity: 0.8;
      margin: 1.0 4.0;
    }

    &:not(clearDownloadsButton) > .toolbarbutton-text {
      display: none;
    }

    &[type=menu] > .toolbarbutton-menu-dropmarker {
      content: chrome://global/skin/icons/arrow-down-12.svg;
      padding: 0.0;
      margin-inline-end: 2.0;
    }

    &[disabled] > .toolbarbutton-icon,
    &:not(:hover):-moz-window-inactive > .toolbarbutton-icon,
    &[type=menu][disabled] > .toolbarbutton-menu-dropmarker,
    &:not(:hover):-moz-window-inactive[type=menu] > .toolbarbutton-menu-dropmarker {
      opacity: 0.5;
    }

    &:-moz-window-inactive[disabled] > .toolbarbutton-icon,
    &:-moz-window-inactive[type=menu][disabled] > .toolbarbutton-menu-dropmarker {
      opacity: 0.25;
    }

    > menupopup {
      margin-top: 1.0;
    }
  }
}

placesList {
  background-color: -moz-sidebar;
  width: 160.0;
  min-width: 100.0;
  max-width: 400.0;

  > treechildren::-moz-tree-cell-text {
    margin-inline-end: 6.0;
  }

  > treechildren::-moz-tree-cell(separator) {
    cursor: default;
  }

  > treechildren::-moz-tree-separator {
    border-top: 1.0 solid color-mix(in srgb, FieldText 70.0, transparent);
    margin: 0.0 10.0;
  }
}

placesView {
  border-top: none !important;

  > splitter {
    border-inline-start: none !important;
    border-inline-end: 1.0 solid color-mix(in srgb, FieldText 30.0, transparent);
    min-width: 1.0;
    width: 3.0;
    margin-inline-start: -3.0;
    position: relative;
    background-image: none !important;
  }
}

back-button {
  margin-inline-end: 0.0 !important; /* override margin from #placesToolbar > toolbarbutton */

  > .toolbarbutton-icon {
    margin-inline: 3.0 2.0 !important;
  }
}

forward-button {
  margin-inline-start: 0.0 !important; /* override margin from #placesToolbar > toolbarbutton */

  > .toolbarbutton-icon {
    margin-inline: 2.0 3.0 !important;
  }
}

detailsPane {
  border-top: 1.0 solid color-mix(in srgb, FieldText 50.0, transparent);
}

placeContent {
  appearance: none;
  border: none;

  > treechildren::-moz-tree-cell,
  > treechildren::-moz-tree-column {
    border-inline-start: 1.0 solid color-mix(in srgb, FieldText 30.0, transparent);
  }

  > treechildren::-moz-tree-column(first-column),
  > treechildren::-moz-tree-cell(first-column) {
    /* This matches the treecol separator in tree.css */
    border-inline-start: none;
  }

  > treechildren::-moz-tree-cell(separator) {
    border-color: transparent;
  }
}

PopupAutoComplete > richlistbox > richlistitem {
  min-height: 20.0;
  border: 0.0;
  border-radius: 0.0;
  padding: 0.0 1.0;

  --status-text-color: currentColor;
  --status-background-color: rgba(248.0, 232.0, 28.0, 0.2);

  > .ac-site-icon {
    margin-inline: 4.0 0.0;
  }

  > .ac-login-item {
    display: flex;
    justify-content: center;
    align-items: center;
    width: 100.0;

    > .ac-secondary-action {
      visibility: hidden;
      height: 16.0;
      width: 16.0;
      border: 0.0;
      color: inherit;
      -moz-context-properties: fill;
      fill: currentColor;
      margin-inline: 8.0;
      cursor: pointer;
      background: url(chrome://global/skin/icons/settings.svg) center no-repeat;
    }
  }

  &:is(:hover, [selected]) > .ac-login-item > .ac-secondary-action {
    visibility: visible;
  }

  > .ac-title {
    font: icon;
    margin-inline-start: 4.0;
  }

  &[originaltype=importableLearnMore] {
    padding-bottom: 2.0;
    padding-inline-start: 21.0;
  }

  &[originaltype=loginsFooter] {
    justify-content: center;
    color: FieldText;
    min-height: 2.6666;
    border-top: 1.0 solid rgba(38.0,38.0,38.0,0.15);
    background-color: hsla(0.0,0.0,80.0,0.35); /* match arrowpanel-dimmed */

    &:hover,
    &[selected] {
      background-color: hsla(0.0,0.0,80.0,0.5); /* match arrowpanel-dimmed-further */
    }
  }

  &[originaltype=insecureWarning] {
    height: auto;

    & > .ac-title > .ac-text-overflow-container > .ac-title-text {
      text-overflow: initial;
      white-space: initial;
    }

    & > .ac-title > label {
      margin-inline-start: 0.0;
    }
  }

  &[originaltype=loginWithOrigin] > .ac-site-icon,
  &[originaltype=insecureWarning] > .ac-site-icon {
    margin-inline-start: 0.0;
    display: initial;
  }

  &[originaltype=login] > .ac-site-icon,
  &[originaltype=possible-username] > .ac-site-icon,
  > .two-line-wrapper > .ac-site-icon {
    display: block;
    margin-inline: 4.0;
  }

  &[originaltype=possible-username] > .ac-site-icon {
    visibility: hidden;
  }

  /* Autocomplete richlistitem support for a two-line label display */

  > .two-line-wrapper {
    box-sizing: border-box;
    display: flex;
    flex-direction: row;
    gap: 8.0;
    margin: 0.0;
    min-width: 0.0;

    > .labels-wrapper {
      /* The text should flex while the icon should not */
      flex: 1.0;
      /* min-width is needed to get the text-overflow: ellipsis to work for the children */
      min-width: 0.0;

      > .label-row {
        overflow: hidden;
        text-overflow: ellipsis;
        white-space: nowrap;
      }

      .line2-label {
        padding-top: 2.0 !important;
        opacity: 0.6;
      }
    }
  }

  &[originaltype=generatedPassword] > .two-line-wrapper > .labels-wrapper > .generated-password-autosave,
  &[originaltype=importableLearnMore] > .ac-title,
  > .two-line-wrapper > .labels-wrapper > .line2-label {
    padding-top: 2.0 !important;
    opacity: 0.6;
  }

  /* Login form autocompletion (with and without origin showing) and generated passwords */
  &[originaltype=generatedPassword] > .two-line-wrapper > .ac-site-icon,
  &[originaltype=loginWithOrigin] > .two-line-wrapper > .ac-site-icon,
  &[originaltype=login] > .ac-site-icon {
    fill: GrayText;
  }

  &[originaltype=importableLogins] > .two-line-wrapper > .ac-site-icon {
    fill: GrayText;
    list-style-image: chrome://browser/skin/import.svg;
  }

  &[originaltype=generatedPassword][selected] > .two-line-wrapper > .ac-site-icon,
  &[originaltype=importableLogins][selected] > .two-line-wrapper > .ac-site-icon,
  &[originaltype=loginWithOrigin][selected] > .two-line-wrapper > .ac-site-icon,
  &[originaltype=login] > .ac-site-icon[selected] {
    fill: SelectedItemText;
  }

    /* Login form autocompletion */
    > .two-line-wrapper {
      padding: 4.0;
      padding-inline-start: 0.0;
    }

  &[originaltype=action] > .two-line-wrapper {
    flex: 1.0;
  }

  &[originaltype=generatedPassword] {
    &:not([collapsed=true]) {
      /* Workaround bug 451997 and/or bug 492645 */
      display: block;
    }

    > .two-line-wrapper > .labels-wrapper {
      &.line2-label > span {
        /* The font-family is only adjusted on the inner span so that the
           line-height of .line2-label matches that of .line1-label */
        font-family: monospace;
      }

      > .generated-password-autosave > span {
        /* The font-* properties are only adjusted on the inner span so that the
           line-height of .generated-password-autosave matches that of .line1-label */
        font-style: italic;
        font-size: 0.85;
        white-space: normal;
      }
    }
  }

  &[originaltype=login] + richlistitem[originaltype=generatedPassword],
  &[originaltype=loginWithOrigin] + richlistitem[originaltype=generatedPassword] {
    /* Separator between logins and generated passwords. This uses --panel-separator-color from default
     * themes since autocomplete doesn't yet switch to dark. */
    border-top: 1.0 solid hsla(210.0,4.0,10.0,0.14);
  }

  &[originaltype=action] {
    text-align: center;
  }

  /* status items */
  > .ac-status {
    padding: var(--space-xsmall) var(--space-small);
    text-align: center;
    background-color: var(--status-background-color);
    color: var(--status-text-color);
    width: 100.0;
    border-bottom: 1.0 solid rgba(38.0,38.0,38.0,0.15);
    font-size: calc(10.0 / 12.0 * 1.0);
  }

  &:has(> .ac-status) {
    opacity: 1.0;
  }

  &[originaltype=autofill][ac-image]:not([ac-image=]) > .two-line-wrapper {
    display: grid;
    grid-template-columns: 32.0 1.0;

    > .ac-site-icon {
      width: auto;
      height: 16.0;
      max-width: 32.0;
      max-height: 16.0;
    }
  }

  /* Insecure field warning */
  &[originaltype=insecureWarning] {
    background-color: var(--arrowpanel-dimmed);
    border-bottom: 1.0 solid var(--panel-separator-color);
    padding-block: 4.0;

    &[selected] {
      background-color: var(--arrowpanel-dimmed-further);
      color: -moz-DialogText;
    }

    > .ac-title {
      color: var(--grey-60);
      font-size: 1.0;
    }

    > .ac-title .ac-emphasize-text-title {
      font-weight: 600.0;
    }

    &[selected] > .ac-title {
      color: inherit;
    }

    > .ac-site-icon {
      list-style-image: chrome://global/skin/icons/security-broken.svg;
      -moz-context-properties: fill;
      fill: var(--grey-60);
    }
  }
}

.autocomplete-richlistitem {
  &:not([disabled=true]):not([selected]):hover {
    background-color: var(--arrowpanel-dimmed);
  }

  &[selected] {
    background-color: SelectedItem;
    color: SelectedItemText;
  }
}

:root {
  --toolbox-bgcolor: -moz-headerbar;
  --toolbox-textcolor: -moz-headerbartext;
  --toolbox-bgcolor-inactive: -moz-headerbarinactive;
  --toolbox-textcolor-inactive: -moz-headerbarinactivetext;

  &:where([tabsintitlebar]) {
    --toolbox-bgcolor: ActiveCaption;
    --toolbox-textcolor: CaptionText;
    --toolbox-bgcolor-inactive: InactiveCaption;
    --toolbox-textcolor-inactive: InactiveCaptionText;
  }

  --sidebar-background-color: -moz-sidebar;
  --sidebar-text-color: -moz-sidebartext;
  --sidebar-border-color: -moz-sidebarborder;

  --tabs-navbar-separator-color: ThreeDShadow;
  --tabs-navbar-separator-style: solid;

  --short-notification-background: 0250BB;
  --short-notification-gradient: 9059FF;

  --urlbar-box-bgcolor: var(--button-background-color-hover);
  --urlbar-box-focus-bgcolor: var(--button-background-color);
  --urlbar-box-hover-bgcolor: var(--button-background-color-hover);
  --urlbar-box-active-bgcolor: var(--button-background-color-active);
  --urlbar-box-text-color: inherit;
  --urlbar-box-hover-text-color: var(--urlbar-box-text-color);
  --urlbar-icon-fill-opacity: 0.9;
  media (prefers-contrast) {
    --urlbar-icon-fill-opacity: 1.0;
  }

  --content-area-shadow: 0.0 2.0 6.0 0.0 light-dark(rgba(0.0,0.0,0.0,0.2), rgba(0.0,0.0,0.0,0.8));
  --chrome-content-separator-color: light-dark(rgba(0.0,0.0,0.0,0.3), rgba(255.0,255.0,255.0,0.3));

  /* stylelint-disable-next-line media-query-no-invalid */
  media not (-moz-bool-pref: sidebar.revamp) {
    &:not([lwtheme]) {
      --chrome-content-separator-color: ThreeDShadow;
    }
  }

  &[lwtheme] {
    --link-color: light-dark(rgb(0.0, 97.0, 224.0), rgb(0.0, 221.0, 255.0));
    --tabs-navbar-separator-color: light-dark(rgba(0.0,0.0,0.0,0.1), rgba(0.0,0.0,0.0,0.3));
    --attention-dot-color: light-dark(2ac3a2, 54ffbd);

    --toolbox-bgcolor: var(--lwt-accent-color);
    --toolbox-bgcolor-inactive: var(--lwt-accent-color-inactive, var(--lwt-accent-color));
    --toolbox-textcolor: var(--lwt-text-color, currentColor);
    --toolbox-textcolor-inactive: var(--lwt-text-color, currentColor);

    media not (prefers-contrast) {
      --focus-outline-color: light-dark(0061E0, 00DDFF);
    }
  }

  media (prefers-color-scheme: dark) {
    /* Increase the contrast of urlbar boxes in dark mode.
       In practice these are bumped-up --button(-hover)-bgcolor rules */
    --urlbar-box-bgcolor: color-mix(in srgb, currentColor 16.0, transparent);
    --urlbar-box-focus-bgcolor: color-mix(in srgb, currentColor 16.0, transparent);
    --urlbar-box-hover-bgcolor: color-mix(in srgb, currentColor 22.0, transparent);
  }
}

 not ((prefers-contrast) or (-moz-bool-pref: browser.theme.native-theme)) {
  :root:not([lwtheme]) {
    --color-accent-primary: light-dark(rgb(0.0, 97.0, 224.0), rgb(0.0, 221.0, 255.0));
    --button-text-color-primary: light-dark(rgb(251.0, 251.0, 254.0), rgb(43.0, 42.0, 51.0));
    --button-background-color: light-dark(rgba(207.0, 207.0, 216.0, 0.33), rgba(0.0, 0.0, 0.0, 0.33));
    --button-background-color-hover: light-dark(rgba(207.0, 207.0, 216.0, 0.66), rgba(207.0, 207.0, 216.0, 0.2));
    --button-background-color-active: light-dark(rgb(207.0, 207.0, 216.0), rgba(207.0, 207.0, 216.0, 0.4));
    --button-text-color: light-dark(rgb(21.0, 20.0, 26.0), rgb(251.0, 251.0, 254.0));

    --arrowpanel-background: light-dark(white, rgb(66.0, 65.0, 77.0));
    --arrowpanel-color: light-dark(black, rgb(251.0, 251.0, 254.0));
    --arrowpanel-border-color: light-dark(rgb(240.0, 240.0, 244.0), rgb(82.0,82.0,94.0));

    --arrowpanel-dimmed: light-dark(
      color-mix(in srgb, currentColor 12.0, transparent),
      color-mix(in srgb, currentColor 17.0, transparent)
    );
    --arrowpanel-dimmed-further: light-dark(
      color-mix(in srgb, currentColor 20.0, transparent),
      color-mix(in srgb, currentColor 14.0, transparent)
    );

    --input-color: light-dark(rgb(21.0, 20.0, 26.0), rgb(251.0, 251.0, 254.0));
    --input-bgcolor: light-dark(rgb(255.0, 255.0, 255.0), 42414d);

    --tab-selected-bgcolor: light-dark(rgb(255.0, 255.0, 255.0), rgba(106.0, 106.0, 120.0, 0.7));
    --tab-selected-textcolor: light-dark(rgb(21.0, 20.0, 26.0), rgb(255.0, 255.0, 255.0));
    --tabs-navbar-separator-style: none;

    /* stylelint-disable-next-line media-query-no-invalid */
    media not ((-moz-windows-mica) or ((-moz-windows-accent-color-in-titlebar) and (-moz-bool-pref: browser.theme.windows.accent-color-in-tabs.enabled))) {
      --toolbox-bgcolor: light-dark(rgb(240.0, 240.0, 244.0), rgb(28.0, 27.0, 34.0));
      --toolbox-textcolor: light-dark(rgb(21.0, 20.0, 26.0), rgb(251.0, 251.0, 254.0));
      --toolbox-bgcolor-inactive: var(--toolbox-bgcolor);
      --toolbox-textcolor-inactive: var(--toolbox-textcolor);
      &:where([tabsintitlebar]) {
        --toolbox-bgcolor-inactive: light-dark(rgb(235.0, 235.0, 239.0), rgb(31.0, 30.0, 37.0));
      }
    }

    --toolbar-color: light-dark(rgb(21.0, 20.0, 26.0), rgb(251.0, 251.0, 254.0));
    --toolbar-bgcolor: light-dark(f9f9fb, rgb(43.0, 42.0, 51.0));

    --toolbarbutton-icon-fill: light-dark(rgb(91.0, 91.0, 102.0), rgb(251.0, 251.0, 254.0));
    --toolbarbutton-icon-fill-attention: light-dark(rgb(0.0, 97.0, 224.0), rgb(0.0, 221.0, 255.0));
    --toolbarbutton-icon-fill-attention-text: light-dark(rgb(240.0, 240.0, 244.0), rgb(28.0, 27.0, 34.0));

    --toolbar-field-border-color: transparent;
    --toolbar-field-background-color: light-dark(rgba(0.0, 0.0, 0.0, 0.05), rgba(0.0, 0.0, 0.0, 0.3));
    --toolbar-field-color: light-dark(rgb(21.0, 20.0, 26.0), rgb(251.0, 251.0, 254.0));
    --toolbar-field-focus-background-color: light-dark(white, rgb(66.0, 65.0, 77.0));
    --toolbar-field-focus-color: light-dark(black, rgb(251.0, 251.0, 254.0));

    --urlbarView-action-color: light-dark(rgb(91.0, 91.0, 102.0), rgb(191.0, 191.0, 201.0));
    --urlbarView-separator-color: light-dark(rgb(240.0, 240.0, 244.0), rgb(82.0, 82.0, 94.0));
    --urlbarView-highlight-background: light-dark(e0e0e6, rgb(43.0,42.0,51.0));
    --urlbarView-highlight-color: light-dark(rgb(21.0, 20.0, 26.0), rgb(251.0, 251.0, 254.0));
    --urlbarView-hover-background: light-dark(rgb(240.0, 240.0, 244.0), var(--arrowpanel-dimmed));

    --urlbar-box-bgcolor: light-dark(white, rgb(66.0, 65.0, 77.0));
    --urlbar-icon-fill-opacity: 0.72;
    media (prefers-color-scheme: dark) {
      --urlbar-icon-fill-opacity: 1.0;
    }

    /* stylelint-disable-next-line media-query-no-invalid */
    media not (-moz-bool-pref: sidebar.revamp) {
      --chrome-content-separator-color: light-dark(rgb(204.0, 204.0, 204.0), hsl(240.0, 5.0, 5.0));
    }

    --link-color: light-dark(rgb(0.0, 97.0, 224.0), rgb(0.0, 221.0, 255.0));
    --attention-dot-color: light-dark(2ac3a2, 54ffbd);

    --sidebar-background-color: light-dark(white, rgb(28.0, 27.0, 34.0));
    --sidebar-text-color: light-dark(rgb(21.0, 20.0, 26.0), rgb(249.0, 249.0, 250.0));
    --sidebar-border-color: light-dark(rgb(240.0, 240.0, 244.0), rgb(82.0, 82.0, 94.0));
  }
}

:root {
  --urlbarView-hover-background: var(--arrowpanel-dimmed);
  --urlbarView-separator-color: color-mix(in srgb, currentColor 14.0, transparent);

  --urlbarView-highlight-background: SelectedItem;
  --urlbarView-highlight-color: SelectedItemText;

  --urlbarView-secondary-text-color: color-mix(in srgb, currentColor 73.0, transparent);

  --urlbarView-action-color: LinkText;
  --urlbarView-action-slide-in-distance: 200.0;

  --urlbarView-small-font-size: 0.85;

  --urlbarView-results-padding: 4.0;
  --urlbarView-row-gutter: 2.0;
  --urlbarView-item-inline-padding: var(--urlbar-icon-padding);
  --urlbarView-item-block-padding: 6.0;

  /* The inline margins of a row icon. Rows with different icon sizes will define
     different values for these variables so that the icons of all rows are
     center-aligned along one vertical axis. Row icons happen to use the
     `.urlbarView-favicon` class but not all icons are favicons (e.g., rich
     search suggestion icons are images from the search engine). */
  --urlbarView-icon-margin-start: 0.0;
  --urlbarView-icon-margin-end: calc(var(--urlbar-icon-padding) + var(--identity-box-margin-inline));

  /* The size and inline margins of a standard 16px favicon. The margin variables
     can be used to align any 16px content along the vertical icon axis. */
  --urlbarView-favicon-width: 16.0;
  --urlbarView-favicon-margin-start: var(--urlbarView-icon-margin-start);
  --urlbarView-favicon-margin-end: var(--urlbarView-icon-margin-end);

  --urlbarView-rich-suggestion-default-icon-size: 28.0;

  --urlbarView-result-button-size: 24.0;
  --urlbarView-result-button-background-opacity: 60.0;
  --urlbarView-result-button-selected-color: var(--toolbar-field-focus-color);
  --urlbarView-result-button-selected-background-color: color-mix(in srgb, var(--toolbar-field-focus-background-color) var(--urlbarView-result-button-background-opacity), transparent);
  --urlbarView-result-button-hover-color: var(--toolbar-field-focus-background-color);
  --urlbarView-result-button-hover-background-color: color-mix(in srgb, var(--toolbar-field-focus-color) var(--urlbarView-result-button-background-opacity), transparent);

  --urlbarView-labeled-row-margin-top: calc(1.46 + 4.0);
  --urlbarView-labeled-row-label-top: calc(-1.27 - 2.0);
  --urlbarView-labeled-tip-margin-top-extra: 8.0;

  --urlbarView-action-button-background-color: color-mix(in srgb, var(--urlbarView-hover-background) 50.0, transparent);
  --urlbarView-action-button-hover-background-color: var(--urlbarView-hover-background);

  &:-moz-locale-dir(rtl) {
    --urlbarView-action-slide-in-distance: -200.0;
  }

  &[lwtheme] {
    --urlbarView-action-color: light-dark(rgb(91.0, 91.0, 102.0), rgb(191.0, 191.0, 201.0));
    --urlbarView-highlight-background: light-dark(rgb(0.0, 99.0, 255.0), rgb(0.0, 99.0, 225.0));
    --urlbarView-highlight-color: white;
  }

  media (prefers-contrast) {
    --urlbarView-separator-color: color-mix(in srgb, currentColor 86.0, transparent);
    --urlbarView-result-button-background-opacity: 100.0;
    --urlbarView-secondary-text-color: currentColor;
  }

  /* stylelint-disable-next-line media-query-no-invalid */
  media (-moz-bool-pref: browser.urlbar.richSuggestions.featureGate) {
    --urlbarView-favicon-margin-start: calc((var(--urlbarView-rich-suggestion-default-icon-size) - var(--urlbarView-favicon-width)) / 2.0);
    --urlbarView-favicon-margin-end: calc(var(--urlbar-icon-padding) + var(--identity-box-margin-inline) + ((var(--urlbarView-rich-suggestion-default-icon-size) - var(--urlbarView-favicon-width)) / 2.0));
    }
}

.urlbarView {
  /* Don't handle window drag events in case we are overlapping a toolbar */
  -moz-window-dragging: no-drag;

  display: block;
  text-shadow: none;
  overflow: clip;
  margin-inline: calc(5.0 + var(--urlbar-container-padding));
  width: calc(100.0 - 2.0 * (5.0 + var(--urlbar-container-padding)));
  /* Match urlbar-background's border. */
  border-inline: 1.0 solid transparent;

  &[hide-temporarily] {
    display: none;
  }
}

.urlbarView-body-inner {
  width: calc(100.0 + 2.0 * var(--urlbarView-row-gutter));
  margin-inline: calc(-1.0 * var(--urlbarView-row-gutter));

  urlbar[open] > .urlbarView > .urlbarView-body-outer > & {
    border-top: 1.0 solid var(--urlbarView-separator-color);
  }
}

.urlbarView-results {
  padding-block: var(--urlbarView-results-padding);
  white-space: nowrap;

  /* Vertically center the one-offs when no results are present. */
  .urlbarView[noresults] > .urlbarView-body-outer > .urlbarView-body-inner > & {
    padding-block: 0.0;
  }
}

.urlbarView-row {
  --urlbarView-second-line-indent: calc(var(--urlbarView-icon-margin-start) + var(--urlbarView-icon-margin-end) + var(--urlbarView-favicon-width));

  /* Align icons that are smaller than the default rich suggestion icon size
    (28px) with default-size rich suggestion icons. */
  /* stylelint-disable-next-line media-query-no-invalid */
  media (-moz-bool-pref: browser.urlbar.richSuggestions.featureGate) {
    &:not([rich-suggestion]),
    &[rich-suggestion][icon-size=16] {
      --urlbarView-icon-margin-start: var(--urlbarView-favicon-margin-start);
      --urlbarView-icon-margin-end: var(--urlbarView-favicon-margin-end);
    }
    &[rich-suggestion][icon-size=24] {
      --urlbarView-icon-margin-start: calc((var(--urlbarView-rich-suggestion-default-icon-size) - 24.0) / 2.0);
      --urlbarView-icon-margin-end: calc(var(--urlbar-icon-padding) + var(--identity-box-margin-inline) + ((var(--urlbarView-rich-suggestion-default-icon-size) - 24.0) / 2.0));
    }
  }

  display: flex;
  align-items: center;
  fill: currentColor;
  fill-opacity: var(--urlbar-icon-fill-opacity);

  /* Use a transparent border rather than margin such that the view's entire
     hover area is contiguous and a row is always hovered as the user moves the
     mouse across rows, even though each row's highlighted area is smaller. */
  border: var(--urlbarView-row-gutter) solid transparent;
  border-radius: calc(var(--urlbarView-row-gutter) + var(--toolbarbutton-border-radius));
  background-clip: padding-box;

  &[row-selectable]:not([selected]):hover {
    background-color: var(--urlbarView-hover-background);
  }

  &[selected] {
    background-color: var(--urlbarView-highlight-background);
    color: var(--urlbarView-highlight-color);
  }

  &:not([type=tip], [type=dynamic]) {
    :root:not([uidensity=compact]) & {
      min-height: 32.0;
    }
    :root[uidensity=touch] & {
      padding-block: 11.0;
    }
  }

  &[rich-suggestion][type=search] {
    :root:not([uidensity=compact]) & {
      min-height: 46.0;
    }
    :root[uidensity=touch] & {
      padding-block: 4.0;
    }
  }
}

.urlbarView-row-inner,
.urlbarView-no-wrap {
  display: inline-flex;
  flex-wrap: nowrap;
  align-items: baseline;
  justify-content: start;
}

.urlbarView-row-inner {
  flex: 1.0 1.0;
  overflow: hidden;
  padding-inline: var(--urlbarView-item-inline-padding);
  padding-block: var(--urlbarView-item-block-padding);

  :root:not([uidensity=compact]) & {
    min-height: 20.0; /* row min-height 32px - (2 * padding-block 6px) */
  }
}

.urlbarView-no-wrap {
  max-width: 100.0;
  flex-grow: 0.0;
  flex-shrink: 0.0;

  .urlbarView[action-override] .urlbarView-row[has-url] > .urlbarView-row-inner > &,
  .urlbarView-row[has-url]:not([type$=tab]) > .urlbarView-row-inner > &,
  .urlbarView-row[has-url]:is([type=remotetab], [sponsored]):is(:hover, [selected]) > .urlbarView-row-inner > & {
    /* We prioritize icons + title + action over the url, so they can grow freely,
       but the url should never disappear when it's visible */
    max-width: calc(70.0 - 2.0 * (var(--urlbarView-favicon-width) + (6.0 + 2.0)));
  }
}

 screen and (min-height: 600.0) {
  .urlbarView-results[wrap] > .urlbarView-row {
    &:where(:not([rich-suggestion])) > .urlbarView-row-inner {
      flex-wrap: wrap;

      > .urlbarView-no-wrap {
        max-width: 100.0 !important;
        flex-basis: 100.0;
      }
    }

    &[has-url] > .urlbarView-row-inner > .urlbarView-url {
      margin-top: 2.0;

      /* urlbarView-url is forced to be LTR for RTL locales, so set the padding based on
         the browser's directionality. */
      &:-moz-locale-dir(ltr) {
        margin-left: var(--urlbarView-second-line-indent);
      }
      &:-moz-locale-dir(rtl) {
        margin-right: var(--urlbarView-second-line-indent);
      }
    }

    /* Note: switchtab entries show the url only in override mode,
       remotetab and sponsored ones only when selected or :hover. */
    .urlbarView[action-override] &[has-url]:not([restyled-search]),
    &[has-url]:not([type$=tab], [sponsored], [restyled-search]),
    &[has-url]:is([type=remotetab], [sponsored]):is(:hover, [selected]),
    &[type=tabtosearch] {
      /* This targets both rich and non-rich results, so not using the child selector here. */
      .urlbarView-title-separator {
        display: none;
      }
    }

    &[type=tabtosearch] > .urlbarView-row-inner > .urlbarView-no-wrap {
      flex-wrap: wrap;

      > .urlbarView-action {
        flex-basis: 100.0;
        margin-inline-start: var(--urlbarView-second-line-indent);
      }
    }

    &[rich-suggestion] > .urlbarView-row-inner > .urlbarView-row-body > .urlbarView-row-body-top {
      flex-wrap: wrap;

      > .urlbarView-row-body-top-no-wrap {
        flex-basis: 100.0;
      }

      > .urlbarView-url {
        margin-top: 2.0;
      }
    }
  }
}

.urlbarView-url:-moz-locale-dir(rtl) {
  /* .urlbarView-url can grow due to `flex-grow: 1`, so without `text-align:
     right`, the URL text would be left-aligned within .urlbarView-url for RTL
     due to the `direction: ltr` rule.  .urlbarView-title does not have this
     problem since it does not have flex. */
  text-align: right;
}

.urlbarView-favicon {
  width: var(--urlbarView-favicon-width);
  height: var(--urlbarView-favicon-width);
  align-self: center;
  margin-inline-start: var(--urlbarView-icon-margin-start);
  margin-inline-end: var(--urlbarView-icon-margin-end);
  background-repeat: no-repeat;
  background-size: contain;
  object-fit: contain;
  flex-shrink: 0.0;
  -moz-context-properties: fill, fill-opacity;

  .urlbarView-row[tail-suggestion] > .urlbarView-row-inner > .urlbarView-no-wrap > & {
    visibility: hidden;
  }

  .urlbarView-row[type=tabtosearch]:not([selected]) > .urlbarView-row-inner > .urlbarView-no-wrap > & {
    /* We use the URL color for this icon to inherit its accessibility
       properties, like adapting to high contrast modes. We also want to ensure
       contrast from the result highlight. */
    color: var(--link-color);
    -moz-context-properties: fill;
  }
}

.urlbarView-type-icon {
  position: absolute;
  width: 12.0;
  height: 12.0;
  align-self: center;
  margin-top: var(--urlbarView-favicon-width);
  margin-inline-start: calc(var(--urlbarView-icon-margin-start) + var(--urlbarView-favicon-width) - 6.0);
  background-repeat: no-repeat;
  background-size: contain;
  -moz-context-properties: fill, stroke;
  stroke: var(--toolbar-field-focus-background-color);

  /* Favicon badges have this priority: pinned > bookmark. */
  .urlbarView-row[type=bookmark] > .urlbarView-row-inner > .urlbarView-no-wrap > & {
    background-image: chrome://browser/skin/bookmark-12.svg;
    fill: var(--toolbarbutton-icon-fill-attention);
  }

  .urlbarView-row[pinned] > .urlbarView-row-inner > .urlbarView-no-wrap > & {
    background-image: chrome://browser/skin/pin-12.svg;
    fill: light-dark(rgb(91.0, 91.0, 102.0), rgb(251.0, 251.0, 254.0));
  }
}

.urlbarView-button {
  display: inline-block;
  min-width: var(--urlbarView-result-button-size);
  min-height: var(--urlbarView-result-button-size);
  background-size: 16.0;
  background-position: center;
  background-repeat: no-repeat;
  margin-inline: 4.0;
  -moz-context-properties: fill, fill-opacity;
  border-radius: 100.0;

  &:not(:hover, [selected], [primary], [open]) {
    media (prefers-contrast) {
      outline: 1.0 solid currentColor;
      outline-offset: -1.0;
    }
  }

  &[selected] {
    outline: var(--focus-outline);
    outline-color: var(--urlbarView-highlight-color);
    outline-offset: 1.0;
  }

  .urlbarView-row:is([row-selectable]:hover, [selected]) > &:not(:hover, [open]) {
    color: var(--urlbarView-result-button-selected-color);
    background-color: var(--urlbarView-result-button-selected-background-color);
  }

  &:is(:hover, [open]) {
    color: var(--urlbarView-result-button-hover-color);
    background-color: var(--urlbarView-result-button-hover-background-color);
  }

  & + & {
    margin-inline-start: 0.0;
  }
}

.urlbarView-button-menu {
  background-image: url(chrome://global/skin/icons/more.svg);
  background-color: var(--button-background-color);

  .urlbarView-results:not([disable-resultmenu-autohide]) > .urlbarView-row:not(:hover, [selected]) > .urlbarView-row-inner + &:not([open]):empty {
    display: none;
  }

  /* Labeled result menu button */

  &:not(:empty) {
    .urlbarView-results:not([wrap]) > .urlbarView-row > & {
      display: inline-flex;
      align-items: center;
      border-radius: var(--urlbarView-result-button-size);
      padding-inline: 8.0 26.0;
      background-position-x: right 4.0;
      font-size: var(--urlbarView-small-font-size);

      &:-moz-locale-dir(rtl) {
        background-position-x: left 4.0;
      }
    }

    .urlbarView-results[wrap] > .urlbarView-row > & {
      /* Hide the label in narrow windows. */
      font-size: 0.0;
    }
  }
}

.urlbarView-button:not(:empty):not(.urlbarView-button-menu) {
  border-radius: var(--toolbarbutton-border-radius);
  padding: 0.5 1.0;
  margin-inline-end: 1.4;
  font-size: 0.93;
  font-weight: var(--font-weight-bold);
  background-clip: padding-box;
  min-height: 16.0;
  min-width: 6.0;
  text-align: center;
  flex-basis: initial;
  flex-shrink: 0.0;

  &:not(:hover) {
    background-color: var(--button-background-color);
  }

  &:is([selected], [primary]) {
    color: var(--button-text-color-primary);
    background-color: var(--color-accent-primary);
    outline-color: var(--color-accent-primary);
    outline-offset: var(--focus-outline-offset);

    &:hover {
      background-color: var(--color-accent-primary-hover);
    }

    &:hover:active {
      background-color: var(--color-accent-primary-active);
    }
  }
}

.urlbarView-row[type=tip] {
  padding-block: 18.0;
  border-block: 0.0;
  border-radius: 0.0;

  &:not(:last-child) {
    border-bottom: 1.0 solid var(--urlbarView-separator-color);
    margin-bottom: 4.0;
  }

  &:not(:first-child) {
    border-top: 1.0 solid var(--urlbarView-separator-color);
    margin-top: 4.0;
  }

  /* For tips with row labels, move the entire row down by an additional
     var(--urlbarView-labeled-tip-margin-top-extra) so there's more space
     between the tip's top border and the label. They're too close otherwise.
     The row label must also be adjusted; see the row label rules. */
  &[label] {
    margin-top: calc(var(--urlbarView-labeled-row-margin-top) + var(--urlbarView-labeled-tip-margin-top-extra));
  }

  > .urlbarView-row-inner {
    min-height: 32.0;
    align-items: center;

    /* Add space between the tip title (and the rest of row-inner) and its
       button. */
    margin-inline-end: 1.8;

    > .urlbarView-no-wrap > .urlbarView-title-separator,
    > .urlbarView-url {
      display: none;
    }

    > .urlbarView-no-wrap > .urlbarView-title {
      white-space: normal;
    }
  }

  .urlbarView-results[wrap] > & {
    display: block;
    text-align: end;

    > .urlbarView-row-inner {
      display: block;
      text-align: start;
    }

    > .urlbarView-button {
      margin-top: 1.0;
      margin-bottom: var(--urlbarView-item-block-padding);
    }
  }

  &:not([tip-type=dismissalAcknowledgment]) > .urlbarView-row-inner > .urlbarView-no-wrap > .urlbarView-favicon {
    width: 24.0;
    height: 24.0;
    margin-inline-end: 12.0;
    flex-basis: 24.0;
    flex-grow:  0.0;
  }

  &[tip-type=dismissalAcknowledgment] {
    padding-block: 6.0;

    &:last-child {
      padding-block-end: max(0.0, 6.0 - var(--urlbarView-results-padding));
    }
  }
