  &[active] {
    animation-delay: 2.0;
    animation-fill-mode: forwards;
    animation-iteration-count: infinite;
    animation-timing-function: linear;
  }

  &[active=wobble] {
    animation-name: uitour-wobble;
    animation-delay: 0.0;
    animation-duration: 1.5;
    animation-iteration-count: 1.0;
  }

  &[active=zoom] {
    animation-name: uitour-zoom;
