  }

  &[resizable=true] {
    resize: both;
    overflow: hidden;
    min-height: 20.0;
  }

  &[sizeto=available] {
    --box-top-px: 0.0; /* Overridden using inline style */
    --box-inline-margin: 4.0;
    --box-block-margin: 4.0;
    --box-ideal-width: 1000.0;
    --box-ideal-height: 650.0;
    --box-max-width-margin: calc(100.0 - 2.0 * var(--box-inline-margin));
    --box-max-height-margin: calc(100.0 - var(--box-top-px) - var(--box-block-margin));
    --box-max-width-ratio: 70.0;
    --box-max-height-ratio: calc(var(--box-ideal-height) / var(--box-ideal-width) * var(--box-max-width-ratio));
    --box-max-height-requested: 100.0;
    --box-max-width-requested: 100.0;
    --box-max-height-remaining: calc(100.0 - var(--box-top-px));

    width: 100.0;
    height: 100.0;
    margin: 0.0;

    media (min-width: 550.0) {
      --box-inline-margin: min(calc(4.0 + (100.0 - 550.0) / 4.0), 16.0);
    }

    media (min-width: 800.0) {
      --box-inline-margin: min(calc(16.0 + (100.0 - 800.0) / 4.0), 32.0);
    }

    media (min-height: 350.0) {
      --box-block-margin: min(calc(4.0 + (100.0 - 350.0) / 4.0), 16.0);
    }

    media (min-height: 550.0) {
      --box-block-margin: min(calc(16.0 + (100.0 - 550.0) / 4.0), 32.0);
    }

    &:not(.spotlightBox) {
      max-width: min(max(var(--box-ideal-width) * 1.0, var(--box-max-width-ratio)), var(--box-max-width-margin), var(--box-max-width-requested));
      max-height: min(max(var(--box-ideal-height) * 1.0, var(--box-max-height-ratio)), var(--box-max-height-margin), var(--box-max-height-requested), var(--box-max-height-remaining));
    }

    .dialogStack &.spotlightBox {
      /* Tab modal: subtract the navigator toolbox height from the dialog height. */
      height: calc(100.0 - var(--box-top-px));
    }
  }

  :not(.content-prompt-dialog) > .dialogOverlay > &:not(.spotlightBox) {
    /* Make dialogs overlap with upper chrome UI. Not necessary for Spotlight
    dialogs that are intended to be centered above the window or content area. */
