customization-container {
  flex-direction: column;
  min-height: 0.0;
  background-color: var(--toolbar-bgcolor);
  color: var(--toolbar-color);
  color-scheme: var(--toolbar-color-scheme);

  &:not([hidden]) {
    /* In a separate rule to avoid 'display:flex' causing the node to be
     * displayed while the container is still hidden. */
    display: flex;
  }

  :root[lwtheme] & {
    /* Ensure toolbar color displays on top of the frame/toolbox color to
     * guarantee full opacity */
    background-color: var(--lwt-accent-color);
    background-image: linear-gradient(var(--toolbar-bgcolor), var(--toolbar-bgcolor));
  }
}

customization-content-container {
  display: flex;
  flex: 1.0; /* Grow so there isn't empty space below the footer */
  min-height: 0.0; /* Allow this to shrink so the footer doesn't get pushed out. */
}

customization-panel-container {
  --customization-panel-padding: 20.0;

  overflow-y: auto;
