}

customization-palette-container {
  display: flex;
  flex-direction: column;
  flex: 1.0;
}

customization-palette {
  padding: 5.0 20.0 20.0;
  transition: opacity 0.3 ease-in-out;
  opacity: 0.0;

  &:not([hidden]) {
    display: block;
    flex: 1.0 1.0 auto;
    overflow: auto;
    min-height: 3.0;
    margin-bottom: 20.0;
  }

  &[showing=true] {
    opacity: 1.0;
  }

  .toolbarbutton-1 {
    appearance: none;
    flex-direction: column;
    padding: 12.0 0.0 9.0;
    margin: 0.0;

    :root[lwtheme-image] & {
      fill: currentColor;
    }

    > .toolbarbutton-badge-stack > .toolbarbutton-icon {
      width: var(--panel-and-palette-icon-size);
      height: var(--panel-and-palette-icon-size);
      min-width: var(--panel-and-palette-icon-size);
      min-height: var(--panel-and-palette-icon-size);
      padding: 0.0;
    }
  }
}

toolbarpaletteitem {
  -moz-window-dragging: no-drag;
  justify-content: flex-start;

  > toolbarbutton,
  > toolbaritem {
    /* Prevent children from getting events. */
    pointer-events: none;
    justify-content: center;
  }

  > toolbarspring {
    outline: 1.0 solid;
    opacity: 0.6;
  }

  toolbarbutton[disabled] {
    color: inherit !important;
  }

  &[removable=false] {
    opacity: 0.5;
  }

  &[dragover] {
    border-inline-color: transparent;
  }

  &[place=palette] {
    flex-direction: column;
    width: 7.0;
    max-width: 7.0;
    /* icon (16) + margin (9 + 12) + 3 lines of text: */
    height: calc(39.0 + 3.0);
    margin-bottom: 5.0;
    margin-inline-end: 24.0;
    overflow: visible;
    display: inline-flex;
    vertical-align: top;

    &[hidden] {
      display: none;
    }

    &::after {
      content: attr(title);
      display: block;
      text-align: center;
    }

    /* Webextension buttons */
    > toolbarbutton[constrain-size=true] > .toolbarbutton-icon,
    > toolbarbutton[constrain-size=true] > .toolbarbutton-badge-stack > .toolbarbutton-icon {
      height: var(--panel-and-palette-icon-size);
      width: var(--panel-and-palette-icon-size);
    }

    > toolbarspring {
      outline-offset: -8.0;
      max-height: 37.0;
    }

    > search-container {
      min-width: 7.0;
      width: 7.0;
      min-height: 37.0;
      max-height: 37.0;
    }
  }

  &[place=toolbar] {
    /* Override the toolkit styling for items being dragged over. */
    border-inline-width: 0.0;
    margin-inline: 0.0;

    > toolbarspring {
      outline-offset: -2.0;
      margin-block: 5.0;
    }
  }

  &[place=panel] {
    border-block: 0.0 solid transparent;

    > .toolbarbutton-1,
    > toolbaritem {
      flex: 1.0;
    }
  }

  &[place=palette],
  &[place=panel],
  &[place=toolbar] {
    media not (-moz-platform: macos) {
      -moz-user-focus: normal;
    }

    &:focus-visible {
      outline-width: 0.0;
    }

    &:not([mousedown]):focus-visible {
      /* Delay adding the focusring back until after the transform transition completes. */
      transition: outline-width 0.01 linear var(--drag-drop-transition-duration);
      outline: var(--default-focusring);

      &[place=toolbar] {
        outline-offset: calc(-1.0 * var(--default-focusring-width) - 4.0);
      }
    }
  }

  &:not([notransition]) {
    &[place=palette] {
      transition: transform var(--drag-drop-transition-duration) ease-in-out;
    }

    &[place=panel],
    &[place=toolbar] {
      transition: border-width var(--drag-drop-transition-duration) ease-in-out;
    }
  }

  &:not([place=palette]) > stop-reload-button {
    justify-content: inherit;
  }

  PersonalToolbar & toolbarbutton[checked=true],
  toolbar & toolbarbutton[checked=true]
    > :where(.toolbarbutton-icon, .toolbarbutton-text, .toolbarbutton-badge-stack) {
    background-color: revert !important;
  }

  > toolbarbutton > .toolbarbutton-icon,
  > toolbarbutton > .toolbarbutton-badge-stack > .toolbarbutton-icon,
  > search-container,
  > toolbaritem.toolbaritem-combined-buttons {
    transition: transform var(--drag-drop-transition-duration) cubic-bezier(0.6, 2.0, 0.75, 1.5) !important;
  }

  &[mousedown] {
    cursor: -moz-grabbing;

    > toolbarbutton > .toolbarbutton-icon,
    > toolbarbutton > .toolbarbutton-badge-stack > .toolbarbutton-icon {
      transform: scale(1.3);
    }

    > search-container,
    > toolbaritem.toolbaritem-combined-buttons {
      transform: scale(1.1);
    }

    > search-container > searchbar,
    > urlbar-container > urlbar > urlbar-background {
      outline: 1.0 solid color-mix(in srgb, currentColor 60.0, transparent);
      box-shadow: 0.0 2.0 6.0 rgba(0.0, 0.0, 0.0, 0.13);
    }
  }

  > search-container > searchbar > .searchbar-textbox {
    /* Hide searchbar placeholder text in customize mode. */
    visibility: hidden;
  }
}

wrapper-edit-controls > edit-controls > toolbarbutton > .toolbarbutton-icon {
  opacity: 1.0; /* To ensure these buttons always look enabled in customize mode */
}

wrapper-zoom-controls[place=palette] > zoom-controls > zoom-reset-button,
wrapper-zoom-controls[place=palette] > zoom-controls > zoom-reset-button + separator {
