customization-panelWrapper,
customization-panelWrapper > .panel-arrowcontent,
customization-panelHolder {
  flex-direction: column;
  display: flex;
  flex-shrink: 1.0;
  min-height: calc(174.0 + 9.0);
}

customization-panelWrapper {
  flex: 1.0;
  align-items: end; /* align to the end on the cross-axis (affects arrow) */

  > .panel-arrowcontent {
    color: var(--arrowpanel-color);
    background: var(--arrowpanel-background);
    background-clip: padding-box;
    max-width: 29.0;

    media (-moz-platform: macos) {
      /* Native styling adds more 'oompf' to the popup box-shadow, so simulate that
       * as best as we can here: */
      box-shadow: 0.0 0.0 1.0 hsla(0.0,0.0,0.0,0.3), 0.0 4.0 10.0 hsla(0.0,0.0,0.0,0.3);
      appearance: none;
      border-radius: var(--arrowpanel-border-radius);
    }

    media not (-moz-platform: macos) {
      border: 1.0 solid var(--arrowpanel-border-color);
      margin: 0.0;
      box-shadow: 0.0 0.0 4.0 hsla(0.0,0.0,0.0,0.2);
    }
  }
}

customization-panelWrapper > .panel-arrowbox {
  display: block;
  position: relative;
  height: 10.0;
  margin-bottom: -1.0;

  > .panel-arrow[side=top] {
    list-style-image: url(chrome://global/skin/arrow/panelarrow-vertical.svg);
    -moz-context-properties: fill, stroke;
    fill: var(--arrowpanel-background);
    stroke: var(--arrowpanel-border-color);
    /* JS code sets --panel-arrow-offset to the distance between the middle of the
     * overflow button and the end of the window. We subtract the padding of our
     * container (#customization-panel-container) and half our own width:
     */
    margin-inline-end: calc(var(--panel-arrow-offset) - var(--customization-panel-padding) - 10.0);
    vertical-align: top;

    media (-moz-platform: macos) {
      /* The OS X image is 2px narrower than the windows/linux one.
       * Add padding to compensate: */
      padding: 0.0 1.0;
      /* specify width for hidpi image to fit correctly */
      width: 20.0;
    }
  }
}

customization-panelHolder {
  padding-block: 10.0;

  > widget-overflow-fixed-list {
    flex: 1.0; /* Grow within the available space, and allow ourselves to shrink */
    display: flex;
    flex-direction: column;
    overflow-y: auto;
    overflow-x: hidden;
    padding-top: 10.0;

    /**
     * We create a ::before pseudoelement that contains a background image to show the
     * drop dragon. This element fades in and out depending on whether the containing
     * panel list is empty and unhovered, or not.
     */
    &:not(:empty) {
      padding-bottom: 50.0; /* Make sure there's always space to drop stuff. */
      border-top: 1.0 solid var(--panel-separator-color);
    }

    &::before {
      display: block;
      content: ;
      background-image: image-set(
        url(chrome://browser/skin/customizableui/empty-overflow-panel.png),
        url(chrome://browser/skin/customizableui/empty-overflow-panel@2x.png) 2.0
      );
      background-position: center bottom 10.0;
      background-size: 218.0 134.0;
      background-repeat: no-repeat;
      opacity: 0.0;
      transition: opacity 300.0 ease-out;
      padding-bottom: 154.0; /* 154 = 134 for the image, 10px space on either side. */
      margin-bottom: -154.0; /* don't affect positioning of the actual contents */
      pointer-events: none;
    }

    &:empty::before {
      opacity: 1.0;
    }
    &[draggingover]:empty::before {
      opacity: 0.0;
    }
  }
}

whimsy-button {
