}

 game-over {
  0.0 {
    transform: scale(1.0) translateY(0.0) rotateZ(0.0);
    opacity: 1.0;
  }
  15.0 {
    transform: scale(2.0) translateY(-10.0) rotateZ(-90.0);
  }
  35.0 {
    opacity: 1.0;
  }
  65.0 {
    opacity: 0.0;
  }
  100.0 {
    transform: scale(2.0) translateY(300.0) rotateZ(-2.5);
    opacity: 0.0;
  }
}

:root {
  --panelui-subview-transition-duration: 150.0;

  --panel-item-hover-bgcolor: var(--button-background-color-hover);
  --panel-item-active-bgcolor: var(--button-background-color-active);
  --panel-banner-item-color: var(--button-text-color);
  --panel-banner-item-background-color: var(--button-background-color);
  --panel-banner-item-hover-bgcolor: var(--button-background-color-hover);
  --panel-banner-item-active-bgcolor: var(--button-background-color-active);
  --panel-banner-item-update-supported-bgcolor: light-dark(2ac3a2, 54ffbd);
  --panel-banner-item-info-icon-bgcolor: light-dark(0090ed, 80ebff);

  --menu-panel-width: 22.35;
  --menu-panel-width-wide: 29.0;

  --arrowpanel-menuitem-margin-block: 0.0;
  --arrowpanel-menuitem-margin-inline: 8.0;
  --arrowpanel-menuitem-margin: var(--arrowpanel-menuitem-margin-block) var(--arrowpanel-menuitem-margin-inline);
  --arrowpanel-menuitem-padding-block: 8.0;
  --arrowpanel-menuitem-padding-inline: 8.0;
  --arrowpanel-menuitem-padding: var(--arrowpanel-menuitem-padding-block) var(--arrowpanel-menuitem-padding-inline);
  --arrowpanel-menuitem-border-radius: 4.0;
  --arrowpanel-header-info-icon-padding: 4.0;
  --arrowpanel-header-info-icon-full-width: calc(16.0 + (2.0 * var(--arrowpanel-header-info-icon-padding)));
  --panel-separator-margin-vertical: 4.0;
  --panel-separator-margin-horizontal: 8.0;
  --panel-separator-margin: var(--panel-separator-margin-vertical) var(--panel-separator-margin-horizontal);
  --panel-subview-body-padding-block: 8.0;
  --panel-subview-body-padding-inline: 0.0;
  --panel-subview-body-padding: var(--panel-subview-body-padding-block) var(--panel-subview-body-padding-inline);

  --panel-and-palette-icon-size: 16.0;

  &:not([lwtheme]) {
    --panel-separator-zap-gradient: linear-gradient(90.0, 9059FF 0.0, FF4AA2 52.08, FFBD4F 100.0);
  }

  &[uidensity=compact] {
    --arrowpanel-menuitem-margin-inline: 4.0;
    --arrowpanel-menuitem-padding-block: 0.0;
    --panel-separator-margin-horizontal: 4.0;
    --panel-subview-body-padding-block: 4.0;
    :is(panel, menupopup):not([touchmode]) {
      --arrowpanel-header-back-icon-padding: 4.0;
    }
  }
}

panel,
menupopup {
  /* The value for the header back icon padding is selected in a way that the
   * height of the header and its separator will be equal to the panel inner
   * top padding plus standard menuitem, so that the header's separator will
   * be located excatly where a normal toolbarseparator would be located after
   * the first menuitem, in a menu without a header. */
  --arrowpanel-header-back-icon-padding: 6.0;
  --arrowpanel-header-back-icon-full-width: calc(16.0 + (2.0 * var(--arrowpanel-header-back-icon-padding)));
  /* The min-height is calculated with the separator's horizontal margin rather
   * than the vertical, to let the back icon have even spacing all around
   * while being aligned with the separator. */
  --arrowpanel-header-min-height: calc(var(--arrowpanel-header-back-icon-full-width)
                                       + (var(--panel-separator-margin-horizontal) * 2.0));
}

PanelUI-menu-button {
