confirmation-hint-checkmark-animation-container {
  position: relative;
  overflow: hidden;
  width: 14.0;
  height: 14.0;

  &[animate] > confirmation-hint-checkmark-image {
    display: block;
    position: absolute;
    background-image: chrome://browser/skin/success-animation.svg;
    background-repeat: no-repeat;
    /* frames are 16px wide by 14px high */
    min-width: 304.0;
    max-width: 304.0;
    min-height: 14.0;
    max-height: 14.0;
    animation-name: confirmation-hint-checkmark-animation;
    animation-fill-mode: forwards;
    animation-timing-function: steps(18.0);
    -moz-context-properties: fill;
    fill: currentColor;

    &:-moz-locale-dir(rtl) {
      animation-name: confirmation-hint-checkmark-animation-rtl;
      transform: translateX(288.0);
    }

    media (prefers-reduced-motion: no-preference) {
      animation-duration: 300.0;
      animation-delay: 60.0;
    }
  }
}

