  appearance: auto;
  -moz-default-appearance: toolbarbutton;

  &:hover {
    color: ButtonText;
  }

  &:hover:active,
  &[open] {
    color: -moz-buttonactivetext;
  }

  &::after {
    display: flex;
    width: 12.0;
    height: 12.0;
    content: url(chrome://global/skin/icons/arrow-down-12.svg);
    -moz-context-properties: fill;
    fill: currentColor;
  }
}

windows.snippet:
menubar-items {
  flex-direction: column; /* for flex hack */
  justify-content: normal; /* align the menubar to the top also in customize mode */
}

main-menubar > menu {
  appearance: none;
  color: inherit;

  &[_moz-menuactive] {
    background-color: light-dark(hsla(0.0,0.0,0.0,0.12), hsla(0.0,0.0,100.0,0.22));
    color: inherit;

    media (prefers-contrast) {
      background-color: -moz-menuhover;
      color: -moz-menuhovertext;
    }
  }
}

 (-moz-windows-accent-color-in-titlebar) or (-moz-windows-mica) {
  :root[tabsintitlebar] {
    media (-moz-windows-mica) {
      &:not([lwtheme]) {
        background-color: transparent;

        /* Accent color in titlebar overrides Mica. */
        media not (-moz-windows-accent-color-in-titlebar) {
          /* Hide the Firefox buttons since Windows draws their own.
           * FIXME(emilio): Find a way of hiding the native buttons, or make them
           * taller, this is very hacky. */
          .titlebar-buttonbox {
            opacity: 0.0;
          }
        }
      }
    }

    /* stylelint-disable-next-line media-query-no-invalid */
    media (-moz-windows-mica) or (-moz-bool-pref: browser.theme.windows.accent-color-in-tabs.enabled) {
      &:not([lwtheme]) TabsToolbar {
        /* These colors match the Linux/HCM default button colors. We need to
         * override these on the tabs toolbar because the accent color is
         * arbitrary, so the hardcoded colors from browser-custom-colors might
         * not provide sufficient contrast. */
        --toolbarbutton-icon-fill: currentColor;
        --toolbarbutton-hover-background: color-mix(in srgb, currentColor 17.0, transparent);
        --toolbarbutton-active-background: color-mix(in srgb, currentColor 30.0, transparent);
      }
    }

    &[sizemode=normal] navigator-toolbox {
      border-top: 0.5 solid ActiveBorder;
      &:-moz-window-inactive {
        border-top-color: InactiveBorder;
      }
    }
  }
}

.titlebar-buttonbox {
  appearance: none;
  /* The button box must appear on top of the navigator-toolbox in order for
   * click and hover mouse events to work properly for the button in the restored
   * window state. Otherwise, elements in the navigator-toolbox, like the menubar,
   * can swallow those events.
   */
  z-index: 1.0;
}

.titlebar-button {
  appearance: none;
  border: none;
  margin: 0.0;
  padding: 8.0 17.0;
  -moz-context-properties: stroke;
  stroke: currentColor;

  :root[tabletmode] & {
    display: none;
  }

  &:focus-visible {
    outline: var(--focus-outline);
    outline-offset: var(--focus-outline-inset);
  }

  > .toolbarbutton-icon {
    width: 12.0;
    height: 12.0;

    &:-moz-locale-dir(rtl) {
      transform: scaleX(-1.0);
    }
  }
}

.titlebar-min {
  /* Even though we use appearance: none, -moz-default-appearance is necessary
   * for Windows 11's "snap layouts" feature, see
   * DealWithWindowsAppearanceHacks */
  -moz-default-appearance: -moz-window-button-minimize;
  list-style-image: chrome://browser/skin/window-controls/minimize.svg;
}

 not (prefers-contrast) {
  .titlebar-button:hover {
    background-color: light-dark(hsla(0.0,0.0,0.0,0.12), hsla(0.0,0.0,100.0,0.22));

    &:active {
      background-color: light-dark(hsla(0.0,0.0,0.0,0.22), hsla(0.0,0.0,100.0,0.32));
    }
  }

  .titlebar-close:hover {
    stroke: white;
    background-color: hsl(355.0,86.0,49.0);

    &:active {
      background-color: hsl(355.0,82.0,69.0);
    }
  }
}

 (prefers-contrast) {
  .titlebar-button {
    background-color: -moz-field;
    stroke: ButtonText;

    &:hover {
      background-color: SelectedItem;
      stroke: SelectedItemText;
    }
  }

  .titlebar-min {
    list-style-image: chrome://browser/skin/window-controls/minimize-highcontrast.svg;
  }

  .titlebar-max {
    list-style-image: chrome://browser/skin/window-controls/maximize-highcontrast.svg;
  }

  .titlebar-restore {
    list-style-image: chrome://browser/skin/window-controls/restore-highcontrast.svg;
  }

  .titlebar-close {
    list-style-image: chrome://browser/skin/window-controls/close-highcontrast.svg;
  }
}

 (2.7 <= resolution <= 2.95) {
  /* NB: todo: this should also change padding on the buttons
   * themselves, but without a device to test this on, it's
   * impossible to know by how much. */
  .titlebar-button > .toolbarbutton-icon {
    width: 10.8;
    height: 10.8;
  }
}

.bookmark-item {
  &:is(menu, menuitem) {
    min-width: 0.0;
    max-width: 32.0;
  }

  &:not(.subviewbutton) > .menu-iconic-left {
    margin-block: 0.0;
  }

  &[cutting] {
    > .toolbarbutton-icon,
    > .menu-iconic-left > .menu-iconic-icon {
      opacity: 0.5;
    }

    > .toolbarbutton-text,
    > .menu-iconic-left > .menu-iconic-text {
      opacity: 0.7;
    }
  }

  &:is(toolbarbutton)[dragover=true][open=true] {
    appearance: none;
    background: SelectedItem !important;
    color: SelectedItemText !important;
    fill: SelectedItemText;
  }
}

 (prefers-contrast) {
  :root {
    /* Windows HCM conventions use these colors for chiclets. We can't use them on
       other platforms because AccentColor can be shown atop SelectedItem,
       which has zero contrast. */
    --urlbar-box-hover-bgcolor: SelectedItem;
    --urlbar-box-active-bgcolor: SelectedItem;
    --urlbar-box-hover-text-color: SelectedItemText;
  }

  :root:not([lwtheme]) {
    urlbar:not([focused]) > urlbar-background,
    searchbar:not(:focus-within),
    .findbar-textbox:not(:focus) {
      border-color: ThreeDShadow;
    }
  }
}

 (prefers-contrast) {
  /* For high contrast themes. XXX is this needed anymore? */
  tabbrowser-tabpanels,
  :root[privatebrowsingmode=temporary] tabbrowser-tabpanels {
    background-color: -moz-default-background-color;
  }
}

UITourTooltipButtons {
  /**
   * Override the --arrowpanel-padding so the background extends
   * to the sides and bottom of the panel.
   */
  margin-inline: -10.0;
  margin-bottom: -10.0;
}

panel[touchmode],
menupopup[touchmode] {
  --arrowpanel-menuitem-padding-block: 12.0;
  --arrowpanel-menuitem-padding: var(--arrowpanel-menuitem-padding-block) var(--arrowpanel-menuitem-padding-inline);
  /* The value for the header back icon padding is selected in a way that the
   * height of the header and its separator will be equal to the panel inner
   * top padding plus standard menuitem, so that the header's separator will
   * be located excatly where a normal toolbarseparator would be located after
   * the first menuitem, in a menu without a header. */
  --arrowpanel-header-back-icon-padding: 10.0;
}

menupopup[touchmode] :is(menu, menuitem, menucaption) {
  /* Use the var set in the rule above to increase menu items height in menupopups when
   * they are opened through touch.
   * Panel menu items already have their padding set in panelUI-shared.css with the same var. */
  padding-block: var(--arrowpanel-menuitem-padding-block);
}

contentAreaContextMenu[touchmode] > context-navigation > menuitem {
  /* The navigation menuitems are bigger to begin with because of their inner padding,
   * so calculate the padding-block to match normal menuitems height when in touchmode.
   * 3.5em is the desired menuitem height (~42px), minus the menuitem actual height devided by 2. */
  padding-block: calc((3.5 - 32.0) / 2.0);
}

linux.snippet:
 not (prefers-contrast) {
  :root:not([lwtheme]) {
    media (-moz-gtk-theme-family) {
      --tabs-navbar-separator-style: none;
      media (prefers-color-scheme: light) {
        --urlbar-box-bgcolor: fafafa;
      }
    }
  }
}

menubar-items {
  flex-direction: column; /* for flex hack */
  justify-content: normal; /* align the menubar to the top also in customize mode */
}

:root[tabsintitlebar] {
  background-color: transparent;

  &[sizemode=normal]:not([gtktiledwindow=true]) {
    /* This takes care of drawing our window decorations on X11 */
    -moz-default-appearance: -moz-window-decorations;
    appearance: auto;

    navigator-toolbox,
    dialog::backdrop {
      border-top-left-radius: env(-moz-gtk-csd-titlebar-radius);
      border-top-right-radius: env(-moz-gtk-csd-titlebar-radius);
    }

    /* stylelint-disable-next-line media-query-no-invalid */
    media (-moz-bool-pref: widget.gtk.rounded-bottom-corners.enabled) {
      navigator-toolbox {
        /* The body clip below covers this. */
        border-radius: 0.0;
      }

      body,
      dialog::backdrop {
        /* Use an uniform clip to allow WebRender to optimize it better */
        border-radius: env(-moz-gtk-csd-titlebar-radius);
      }
    }
  }
}

.bookmark-item {
  &:is(menu, menuitem) {
    min-width: 0.0;
    max-width: 32.0;
  }

  &:not(.subviewbutton) > .menu-iconic-left {
    margin-block: 0.0;
  }

  /* Bookmark drag and drop styles */
  &[dragover-into=true] {
    background: SelectedItem !important;
    color: SelectedItemText !important;
  }

  &[cutting] {
    > .toolbarbutton-icon,
    > .menu-iconic-left > .menu-iconic-icon {
      opacity: 0.5;
    }

    > .toolbarbutton-text,
    > .menu-iconic-left > .menu-iconic-text {
      opacity: 0.7;
    }
  }
}

 not (-moz-bool-pref: sidebar.revamp) {
  .sidebar-splitter {
    appearance: none;
    width: 6.0;
    background-color: -moz-dialog;
    border: 1.0 ThreeDShadow;
    border-style: none solid;
  }
}

tabbrowser-tabs {
  /* override the global style to allow the selected tab to be above the nav-bar */
  z-index: auto;
}

.browser-titlebar,
main-menubar {
  appearance: none;
  background-color: transparent;
  color: inherit;
}

UITourHighlight {
  /* Below are some fixes for people without an X compositor on Linux.
     This is why we can't have nice things: */
  /* Animations don't repaint properly without an X compositor. */
  animation-name: none !important;
  /* Opacity rounds to 0 or 1 on Linux without an X compositor, making the
     background color not visible. Anti-aliasing is not available either. Make a
     thicker outline and cancel border-radius for that case. */
     outline: 4.0 solid rgb(0.0,200.0,215.0);
     border-radius: 0.0 !important;
}

.titlebar-button {
  appearance: none;
  align-items: center;
  padding: 0.0;
  padding-inline: calc(env(-moz-gtk-csd-titlebar-button-spacing) / 2.0);

  > .toolbarbutton-icon {
    appearance: auto;
  }

  /* stylelint-disable-next-line media-query-no-invalid */
  media (-moz-bool-pref: widget.gtk.non-native-titlebar-buttons.enabled) {
    /* When using lightweight themes, use our own buttons since native ones might
     * assume a native background in order to be visible. */
    :root[lwtheme] & {
      padding-inline: 3.0;

      > .toolbarbutton-icon {
        appearance: none;
        border-radius: 100.0;
        background-position: center center;
        background-repeat: no-repeat;
        -moz-context-properties: stroke;
        stroke: currentColor;
        /* Roughly the Adwaita size */
        width: 24.0;
        height: 24.0;
      }
    }
  }

  &:hover > .toolbarbutton-icon {
    background-color: color-mix(in srgb, currentColor 12.0, transparent);
  }

  &:hover:active > .toolbarbutton-icon {
    background-color: color-mix(in srgb, currentColor 20.0, transparent);
  }

  &:focus-visible > .toolbarbutton-icon {
    outline: var(--focus-outline);
    outline-offset: var(--focus-outline-inset);
  }
}

.titlebar-close {
  order: env(-moz-gtk-csd-close-button-position);

  > .toolbarbutton-icon {
    -moz-default-appearance: -moz-window-button-close;
    background-image: chrome://browser/skin/window-controls/close.svg;
  }

  &:hover > .toolbarbutton-icon {
    background-color: d70022;
    color: white;
  }

  &:hover:active > .toolbarbutton-icon {
    background-color: ff0039;
  }

  media not (-moz-gtk-csd-close-button) {
    display: none;
  }
}

.titlebar-min {
  order: env(-moz-gtk-csd-minimize-button-position);

  > .toolbarbutton-icon {
    -moz-default-appearance: -moz-window-button-minimize;
    background-image: chrome://browser/skin/window-controls/minimize.svg;
  }

  media not (-moz-gtk-csd-minimize-button) {
    display: none;
  }
}
